import React, { useState, useEffect } from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  description,
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true,
  showStats = false,
  stats = []
}) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Enhanced Interactive Background */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Dynamic floating orbs that follow mouse */}
        <div 
          className="absolute w-96 h-96 rounded-full blur-3xl animate-float-slow transition-all duration-1000"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.08)',
            left: `${mousePosition.x * 0.3}%`,
            top: `${mousePosition.y * 0.2}%`,
            transform: 'translate(-50%, -50%)'
          }}
        ></div>
        <div 
          className="absolute w-80 h-80 rounded-full blur-3xl animate-float-slow transition-all duration-1000"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.06)',
            left: `${100 - mousePosition.x * 0.4}%`,
            top: `${100 - mousePosition.y * 0.3}%`,
            transform: 'translate(-50%, -50%)',
            animationDelay: '2s'
          }}
        ></div>

        {/* Animated gradient mesh */}
        <div className="absolute inset-0 bg-gradient-to-br from-white/5 via-transparent to-white/5 animate-pulse-slow"></div>
      </div>

      {/* Enhanced overlay with parallax effect */}
      <div 
        className="absolute inset-0 bg-cinematic-overlay opacity-25 transition-all duration-1000"
        style={{
          background: `radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, rgba(255,255,255,0.1) 0%, transparent 50%)`
        }}
      ></div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          {/* Enhanced Logo/Brand Element */}
          <div className={`mb-8 animate-fade-in-up ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.1s'}}>
            <div className="inline-flex items-center justify-center w-20 h-20 bg-white/10 backdrop-blur-xl rounded-3xl border border-white/20 shadow-premium mb-6">
              <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
              </svg>
            </div>
          </div>

          {/* Enhanced Title with Better Typography */}
          <h1 className={`text-5xl md:text-7xl lg:text-8xl xl:text-9xl font-black mb-8 ${textColor} animate-fade-in-up text-shadow-lg tracking-tight leading-none ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.2s'}}>
            <span className="block bg-gradient-to-r from-white via-white to-white/90 bg-clip-text text-transparent">
              {title}
            </span>
          </h1>

          {/* Enhanced Subtitle */}
          {subtitle && (
            <div className={`mb-6 animate-fade-in-up ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.3s'}}>
              <p className={`text-xl md:text-2xl lg:text-3xl font-bold ${subtitleColor} tracking-wide`}>
                {subtitle}
              </p>
            </div>
          )}

          {/* Enhanced Description */}
          {description && (
            <div className={`mb-12 animate-fade-in-up ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.4s'}}>
              <p className={`text-lg md:text-xl lg:text-2xl max-w-4xl mx-auto ${subtitleColor} leading-relaxed font-light`}>
                {description}
              </p>
            </div>
          )}

          {/* Enhanced CTA Buttons */}
          {ctaButtons.length > 0 && (
            <div className={`flex flex-col sm:flex-row gap-6 justify-center items-center mb-16 animate-fade-in-up ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.5s'}}>
              {ctaButtons.map((button, index) => {
                // Enhanced button with better animations
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover group relative overflow-hidden`}
                      style={{transitionDelay: `${0.6 + index * 0.1}s`}}
                    >
                      <span className="relative z-10 flex items-center gap-3">
                        {button.text}
                        <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                        </svg>
                      </span>
                      {/* Enhanced shimmer effect */}
                      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover group"
                    style={{transitionDelay: `${0.6 + index * 0.1}s`}}
                  >
                    <span className="flex items-center gap-3">
                      {button.text}
                      <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                      </svg>
                    </span>
                  </Button>
                );
              })}
            </div>
          )}

          {/* Enhanced Stats Section */}
          {showStats && stats.length > 0 && (
            <div className={`grid grid-cols-2 md:grid-cols-4 gap-8 max-w-4xl mx-auto animate-fade-in-up ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '0.7s'}}>
              {stats.map((stat, index) => (
                <div key={index} className="text-center group">
                  <div className="text-3xl md:text-4xl font-black text-white mb-2 group-hover:scale-110 transition-transform duration-300">
                    {stat.value}
                  </div>
                  <div className="text-sm md:text-base text-white/80 font-medium">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Enhanced Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating geometric shapes with enhanced animations */}
        <div className="absolute top-20 left-20 w-32 h-32 bg-white/8 rounded-3xl blur-xl animate-float group-hover:scale-110 transition-transform duration-500"></div>
        <div className="absolute top-40 right-32 w-24 h-24 bg-white/6 rounded-2xl blur-lg animate-float" style={{animationDelay: '2.5s'}}></div>
        <div className="absolute bottom-32 left-32 w-40 h-40 bg-white/5 rounded-3xl blur-2xl animate-float" style={{animationDelay: '1.8s'}}></div>
        <div className="absolute bottom-20 right-20 w-20 h-20 bg-white/7 rounded-xl blur-md animate-float" style={{animationDelay: '0.7s'}}></div>

        {/* Enhanced grid pattern overlay */}
        <div className="absolute inset-0 bg-grid-pattern opacity-3"></div>

        {/* Enhanced dots pattern */}
        <div className="absolute inset-0 bg-dots-pattern opacity-3"></div>

        {/* Animated lines */}
        <div className="absolute top-1/4 left-0 w-full h-px bg-gradient-to-r from-transparent via-white/20 to-transparent animate-pulse"></div>
        <div className="absolute bottom-1/4 left-0 w-full h-px bg-gradient-to-r from-transparent via-white/20 to-transparent animate-pulse" style={{animationDelay: '1s'}}></div>
      </div>

      {/* Enhanced Scroll Indicator */}
      {fullScreen && (
        <div className={`absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`} style={{transitionDelay: '1s'}}>
          <div className="flex flex-col items-center gap-2">
            <div className="text-white/60 text-sm font-medium mb-2">Scroll to explore</div>
            <div className="w-6 h-10 border-2 border-white/50 rounded-full flex justify-center relative group cursor-pointer">
              <div className="w-1 h-3 bg-white/70 rounded-full mt-2 animate-pulse group-hover:bg-white transition-colors duration-300"></div>
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
