import React from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true
}) => {
  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  // Dynamic background colors based on theme
  const getBackgroundColors = () => {
    switch(background) {
      case 'logistics':
        return {
          primary: '#bf2530',
          secondary: '#dc2626',
          tertiary: '#f87171',
          light: '#fee2e2',
          dark: '#991b1b'
        };
      case 'travel':
        return {
          primary: '#1eba55',
          secondary: '#16a34a',
          tertiary: '#4ade80',
          light: '#dcfce7',
          dark: '#15803d'
        };
      default:
        return {
          primary: '#5e716a',
          secondary: '#4b5a54',
          tertiary: '#8da59c',
          light: '#f0f3f2',
          dark: '#38433e'
        };
    }
  };

  const colors = getBackgroundColors();

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Enhanced Dynamic Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Large floating orbs with dynamic colors */}
        <div 
          className="absolute -top-60 -right-60 w-96 h-96 rounded-full blur-3xl animate-float-slow opacity-20"
          style={{ backgroundColor: colors.tertiary }}
        ></div>
        <div 
          className="absolute -bottom-60 -left-60 w-80 h-80 rounded-full blur-3xl animate-float-slow opacity-25"
          style={{ backgroundColor: colors.secondary, animationDelay: '2s' }}
        ></div>
        <div 
          className="absolute top-1/4 -right-32 w-64 h-64 rounded-full blur-2xl animate-float-slow opacity-15"
          style={{ backgroundColor: colors.primary, animationDelay: '4s' }}
        ></div>
        <div 
          className="absolute bottom-1/4 -left-32 w-72 h-72 rounded-full blur-2xl animate-float-slow opacity-20"
          style={{ backgroundColor: colors.tertiary, animationDelay: '1s' }}
        ></div>

        {/* Medium floating elements */}
        <div 
          className="absolute top-20 right-1/4 w-48 h-48 rounded-full blur-xl animate-float-slow opacity-10"
          style={{ backgroundColor: colors.light, animationDelay: '3s' }}
        ></div>
        <div 
          className="absolute bottom-20 left-1/4 w-56 h-56 rounded-full blur-xl animate-float-slow opacity-12"
          style={{ backgroundColor: colors.secondary, animationDelay: '5s' }}
        ></div>

        {/* Small accent orbs */}
        <div 
          className="absolute top-1/3 left-1/3 w-32 h-32 rounded-full blur-lg animate-float-slow opacity-8"
          style={{ backgroundColor: colors.primary, animationDelay: '2.5s' }}
        ></div>
        <div 
          className="absolute bottom-1/3 right-1/3 w-40 h-40 rounded-full blur-lg animate-float-slow opacity-10"
          style={{ backgroundColor: colors.tertiary, animationDelay: '3.5s' }}
        ></div>
      </div>

      {/* Enhanced Premium Overlay */}
      <div 
        className="absolute inset-0 opacity-30"
        style={{
          background: `linear-gradient(135deg, ${colors.primary}40 0%, ${colors.secondary}30 50%, ${colors.tertiary}40 100%)`
        }}
      ></div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          <h1 className={`text-6xl md:text-8xl lg:text-9xl font-black mb-12 ${textColor} animate-fade-in-up text-shadow-lg tracking-tight`}>
            {title}
          </h1>
          {subtitle && (
            <p className={`text-2xl md:text-3xl lg:text-4xl mb-16 max-w-5xl mx-auto ${subtitleColor} animate-fade-in-up leading-relaxed font-light`} style={{animationDelay: '0.3s'}}>
              {subtitle}
            </p>
          )}

          {ctaButtons.length > 0 && (
            <div className="flex flex-col sm:flex-row gap-8 justify-center items-center mb-16 animate-fade-in-up" style={{animationDelay: '0.6s'}}>
              {ctaButtons.map((button, index) => {
                // If button has custom className, render as direct button element
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover`}
                    >
                      <span className="relative z-10 flex items-center gap-2">{button.text}</span>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover"
                  >
                    {button.text}
                  </Button>
                );
              })}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Premium decorative elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating geometric shapes */}
        <div className="absolute top-32 left-32 w-24 h-24 bg-white/10 rounded-2xl blur-xl animate-float"></div>
        <div className="absolute top-48 right-40 w-20 h-20 bg-white/10 rounded-2xl blur-xl animate-float" style={{animationDelay: '2.5s'}}></div>
        <div className="absolute bottom-40 left-40 w-28 h-28 bg-white/10 rounded-2xl blur-xl animate-float" style={{animationDelay: '1.8s'}}></div>
        <div className="absolute bottom-32 right-32 w-16 h-16 bg-white/10 rounded-2xl blur-xl animate-float" style={{animationDelay: '0.7s'}}></div>

        {/* Grid pattern overlay */}
        <div className="absolute inset-0 bg-grid-pattern opacity-5"></div>

        {/* Dots pattern */}
        <div className="absolute inset-0 bg-dots-pattern opacity-5"></div>
      </div>

      {/* Scroll indicator */}
      {fullScreen && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow">
          <div className="w-6 h-10 border-2 border-white/50 rounded-full flex justify-center">
            <div className="w-1 h-3 bg-white/70 rounded-full mt-2 animate-pulse"></div>
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
