import React from 'react';
import { useLocation, useSearchParams } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import LogisticsFooter from './LogisticsFooter';
import Breadcrumb from '../common/Breadcrumb';
import ScrollToTop from '../common/ScrollToTop';

const Layout = ({ children }) => {
  const location = useLocation();
  const [searchParams] = useSearchParams();
  const isLogisticsPage = location.pathname === '/logistics';
  const isTravelPage = location.pathname === '/travel';
  const isContactPage = location.pathname === '/contact';
  const isLogisticsContact = isContactPage && searchParams.get('category') === 'logistics';

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <Breadcrumb />
      <main className="flex-grow">
        {children}
      </main>
      {/* Conditional Footer Rendering */}
      {(isLogisticsPage || isLogisticsContact) && <LogisticsFooter />}
      {!isLogisticsPage && !isTravelPage && !isContactPage && <Footer />}
      {isContactPage && !isLogisticsContact && searchParams.get('category') !== 'travel' && <Footer />}
      <ScrollToTop />
    </div>
  );
};

export default Layout;
