import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Button from './Button';

const PremiumHeroSection = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  const heroSlides = [
    {
      title: "Global Excellence",
      subtitle: "Connecting continents through innovative logistics and travel solutions",
      background: "logistics",
      accent: "#bf2530"
    },
    {
      title: "Seamless Journeys",
      subtitle: "Empowering businesses and individuals to reach their destinations",
      background: "travel", 
      accent: "#1eba55"
    },
    {
      title: "Trusted Partnership",
      subtitle: "Your success is our commitment to excellence and reliability",
      background: "primary",
      accent: "#5e716a"
    }
  ];

  const currentSlideData = heroSlides[currentSlide];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const getBackgroundClass = (background) => {
    switch(background) {
      case 'logistics':
        return 'bg-gradient-to-br from-logistics-600 via-logistics-700 to-logistics-800';
      case 'travel':
