import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

const EnhancedBreadcrumb = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [showQuickNav, setShowQuickNav] = useState(false);

  // Generate breadcrumb items based on current path
  const generateBreadcrumbs = () => {
    const pathSegments = location.pathname.split('/').filter(segment => segment !== '');
    const breadcrumbs = [
      { name: 'Home', path: '/', isActive: location.pathname === '/' }
    ];

    // Build breadcrumb path progressively
    let currentPath = '';
    pathSegments.forEach((segment, index) => {
      currentPath += `/${segment}`;
      const isLast = index === pathSegments.length - 1;

      // Convert segment to readable name
      const readableName = segment
        .split('-')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ');

      breadcrumbs.push({
        name: readableName,
        path: currentPath,
        isActive: isLast
      });
    });

    return breadcrumbs;
  };

  const breadcrumbs = generateBreadcrumbs();

  // Quick navigation menu items
  const quickNavItems = [
    { name: 'Home', path: '/', icon: '🏠' },
    { name: 'About', path: '/about', icon: 'ℹ️' },
    { name: 'Logistics', path: '/logistics', icon: '🚚' },
    { name: 'Travel', path: '/travel', icon: '✈️' },
    { name: 'Contact', path: '/contact', icon: '📞' }
  ];

  // Get current page brand styling
  const getCurrentBrand = () => {
    const path = location.pathname;
    if (path.startsWith('/logistics')) {
      return {
        bgColor: 'bg-logistics-50/90',
        borderColor: 'border-logistics-200',
        textColor: 'text-logistics-700',
        activeTextColor: 'text-logistics-800',
        hoverBgColor: 'hover:bg-logistics-100',
        separatorColor: 'text-logistics-400',
        buttonColor: 'bg-logistics-100 hover:bg-logistics-200 text-logistics-800'
      };
    } else if (path.startsWith('/travel')) {
      return {
        bgColor: 'bg-travel-50/90',
        borderColor: 'border-travel-200',
        textColor: 'text-travel-700',
        activeTextColor: 'text-travel-800',
        hoverBgColor: 'hover:bg-travel-100',
        separatorColor: 'text-travel-400',
        buttonColor: 'bg-travel-100 hover:bg-travel-200 text-travel-800'
      };
    } else {
      return {
        bgColor: 'bg-primary-50/90',
        borderColor: 'border-primary-200',
        textColor: 'text-primary-700',
        activeTextColor: 'text-primary-800',
        hoverBgColor: 'hover:bg-primary-100',
        separatorColor: 'text-primary-400',
        buttonColor: 'bg-primary-100 hover:bg-primary-200 text-primary-800'
      };
    }
  };

  const brandStyles = getCurrentBrand();

  // Don't show breadcrumb on home page
  if (location.pathname === '/') {
    return null;
  }

  return (
    <nav 
      className={`${brandStyles.bgColor} backdrop-blur-xl border-b ${brandStyles.borderColor} shadow-sm sticky top-16 z-40`}
      aria-label="Breadcrumb"
    >
      <div className="container-premium">
        <div className="flex items-center justify-between py-3">
          {/* Breadcrumb Navigation */}
          <div className="flex items-center space-x-2">
            <ol className="flex items-center space-x-2">
              {breadcrumbs.map((breadcrumb, index) => (
                <li key={breadcrumb.path} className="flex items-center">
                  {index > 0 && (
                    <svg 
                      className={`w-4 h-4 mx-2 ${brandStyles.separatorColor}`} 
                      fill="currentColor" 
                      viewBox="0 0 20 20"
                    >
                      <path 
                        fillRule="evenodd" 
                        d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" 
                        clipRule="evenodd" 
                      />
                    </svg>
                  )}

                  {breadcrumb.isActive ? (
                    <span 
                      className={`text-sm font-semibold ${brandStyles.activeTextColor} flex items-center`}
                      aria-current="page"
                    >
                      <svg 
                        className="w-4 h-4 mr-1.5" 
                        fill="currentColor" 
                        viewBox="0 0 20 20"
                      >
                        <path 
                          fillRule="evenodd" 
                          d="M9.504 1.132a1 1 0 01.992 0l1.75 1a1 1 0 11-.992 1.736L10 3.152l-1.254.716a1 1 0 11-.992-1.736l1.75-1zM5.618 4.504a1 1 0 01-.372 1.364L5.016 6l.23.132a1 1 0 11-.992 1.736L3.5 7.723V8a1 1 0 01-2 0V6a.996.996 0 01.52-.878l1.734-.99a1 1 0 011.364.372zm8.764 0a1 1 0 011.364-.372l1.734.99A.996.996 0 0118 6v2a1 1 0 11-2 0v-.277l-1.754 1.145a1 1 0 11-.992-1.736L14.016 6l-.23-.132a1 1 0 01-.372-1.364zm-7 4a1 1 0 011.364-.372L10 8.848l1.254-.716a1 1 0 11.992 1.736L11 10.723V12a1 1 0 11-2 0v-1.277l-1.246-.855a1 1 0 01-.372-1.364zM3 11a1 1 0 011 1v1.277l1.246.855a1 1 0 11-.992 1.736l-1.75-1A.996.996 0 012 14v-2a1 1 0 011-1zm14 0a1 1 0 011 1v2a.996.996 0 01-.504.878l-1.75 1a1 1 0 11-.992-1.736L16.5 13.277V12a1 1 0 011-1zm-9.618 5.504a1 1 0 011.364-.372l.254.145V16a1 1 0 112 0v.277l.254-.145a1 1 0 11.992 1.736l-1.735.992a.995.995 0 01-1.022 0l-1.735-.992a1 1 0 01-.372-1.364z" 
                          clipRule="evenodd" 
                        />
                      </svg>
                      {breadcrumb.name}
                    </span>
                  ) : (
                    <Link
                      to={breadcrumb.path}
                      className={`text-sm font-medium ${brandStyles.textColor} ${brandStyles.hoverBgColor} px-2 py-1 rounded-lg transition-all duration-200 hover:shadow-sm`}
                    >
                      {breadcrumb.name}
                    </Link>
                  )}
                </li>
              ))}
            </ol>
          </div>

          {/* Right Side Controls */}
          <div className="flex items-center space-x-3">
            {/* Quick Navigation Toggle */}
            <button
              onClick={() => setShowQuickNav(!showQuickNav)}
              className={`flex items-center space-x-1 px-3 py-1.5 text-xs font-medium rounded-lg transition-all duration-200 hover:shadow-sm ${brandStyles.buttonColor}`}
              title="Quick navigation menu"
            >
              <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
              <span>Quick Nav</span>
            </button>

            {/* Back Button */}
            {breadcrumbs.length > 1 && (
              <button
                onClick={() => navigate(-1)}
                className={`flex items-center space-x-1 px-3 py-1.5 text-xs font-medium rounded-lg transition-all duration-200 hover:shadow-sm ${brandStyles.textColor} ${brandStyles.hoverBgColor}`}
                title="Go back to previous page"
              >
                <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
                <span>Back</span>
              </button>
            )}

            {/* Page Progress */}
            <div className="flex items-center space-x-2">
              <div className="text-xs text-gray-500 font-medium">
                {breadcrumbs.findIndex(b => b.isActive) + 1}/{breadcrumbs.length}
              </div>
              <div className="flex space-x-1">
                {breadcrumbs.map((_, index) => (
                  <div
                    key={index}
                    className={`w-2 h-2 rounded-full transition-all duration-300 ${
                      index <= breadcrumbs.findIndex(b => b.isActive)
                        ? brandStyles.activeTextColor.replace('text-', 'bg-').replace('-800', '-500')
                        : 'bg-gray-300'
                    }`}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Quick Navigation Dropdown */}
        {showQuickNav && (
          <div className="border-t border-gray-200 pt-3 mt-3 animate-fade-in">
            <div className="flex flex-wrap gap-2">
              {quickNavItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`flex items-center space-x-2 px-3 py-2 text-sm font-medium rounded-lg transition-all duration-200 hover:shadow-sm ${
                    location.pathname === item.path
                      ? brandStyles.buttonColor
                      : `${brandStyles.textColor} ${brandStyles.hoverBgColor}`
                  }`}
                  onClick={() => setShowQuickNav(false)}
                >
                  <span className="text-base">{item.icon}</span>
                  <span>{item.name}</span>
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default EnhancedBreadcrumb;
