import React, { useState, useEffect } from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true,
  showStats = false,
  stats = []
}) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Enhanced Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Dynamic floating orbs with mouse interaction */}
        <div 
          className="absolute -top-40 -right-40 w-80 h-80 bg-white/8 rounded-full blur-3xl animate-float-slow transition-all duration-1000"
          style={{
            transform: `translate(${mousePosition.x * 0.02}px, ${mousePosition.y * 0.02}px)`,
            background: 'radial-gradient(circle, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 50%, transparent 100%)'
          }}
        ></div>
        <div 
          className="absolute -bottom-40 -left-40 w-96 h-96 bg-white/6 rounded-full blur-3xl animate-float-slow transition-all duration-1000" 
          style={{
            animationDelay: '2s',
            transform: `translate(${mousePosition.x * -0.015}px, ${mousePosition.y * -0.015}px)`,
            background: 'radial-gradient(circle, rgba(255,255,255,0.08) 0%, rgba(255,255,255,0.04) 50%, transparent 100%)'
          }}
        ></div>

        {/* Additional floating elements */}
        <div 
          className="absolute top-1/4 left-1/4 w-32 h-32 bg-white/5 rounded-full blur-2xl animate-float-slow"
          style={{ animationDelay: '1s' }}
        ></div>
        <div 
          className="absolute bottom-1/4 right-1/4 w-48 h-48 bg-white/4 rounded-full blur-2xl animate-float-slow"
          style={{ animationDelay: '3s' }}
        ></div>
      </div>

      {/* Enhanced Premium Overlay */}
      <div className="absolute inset-0 bg-cinematic-overlay opacity-25"></div>

      {/* Animated Grid Pattern */}
      <div className="absolute inset-0 bg-grid-pattern opacity-8 animate-pulse-slow"></div>

      {/* Floating Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-1 h-1 bg-white/30 rounded-full animate-float-slow"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}
          ></div>
        ))}
      </div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          {/* Enhanced Title with Gradient Text */}
          <div className={`mb-8 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`}>
            <h1 className={`text-7xl md:text-8xl lg:text-9xl xl:text-[12rem] font-black mb-6 ${textColor} text-shadow-lg tracking-tight leading-none`}>
              <span className="bg-gradient-to-r from-white via-white to-white/90 bg-clip-text text-transparent">
                {title.split(' ').map((word, index) => (
                  <span 
                    key={index} 
                    className="inline-block hover:scale-110 transition-transform duration-500 cursor-default"
                    style={{ animationDelay: `${index * 0.1}s` }}
                  >
                    {word}{index < title.split(' ').length - 1 ? ' ' : ''}
                  </span>
                ))}
              </span>
            </h1>

            {/* Enhanced Subtitle */}
            {subtitle && (
              <div className={`max-w-6xl mx-auto ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.3s'}}>
                <p className={`text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-light leading-tight ${subtitleColor} mb-4`}>
                  {subtitle}
                </p>
                {/* Decorative line */}
                <div className="w-32 h-1 bg-gradient-to-r from-white/60 via-white to-white/60 mx-auto rounded-full"></div>
              </div>
            )}
          </div>

          {/* Enhanced CTA Buttons */}
          {ctaButtons.length > 0 && (
            <div className={`flex flex-col sm:flex-row gap-8 justify-center items-center mb-16 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.6s'}}>
              {ctaButtons.map((button, index) => {
                // If button has custom className, render as direct button element
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover group relative overflow-hidden`}
                      style={{ animationDelay: `${0.8 + index * 0.1}s` }}
                    >
                      <span className="relative z-10 flex items-center gap-3">
                        {button.text}
                        <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                        </svg>
                      </span>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover group"
                    style={{ animationDelay: `${0.8 + index * 0.1}s` }}
                  >
                    <span className="flex items-center gap-3">
                      {button.text}
                      <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                      </svg>
                    </span>
                  </Button>
                );
              })}
            </div>
          )}

          {/* Enhanced Stats Section */}
          {showStats && stats.length > 0 && (
            <div className={`grid grid-cols-2 md:grid-cols-4 gap-8 max-w-4xl mx-auto ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.9s'}}>
              {stats.map((stat, index) => (
                <div key={index} className="text-center group">
                  <div className="text-4xl md:text-5xl font-black text-white mb-2 group-hover:scale-110 transition-transform duration-300">
                    {stat.value}
                  </div>
                  <div className="text-lg text-white/80 font-medium group-hover:text-white transition-colors duration-300">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Enhanced Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating geometric shapes with enhanced animations */}
        <div className="absolute top-32 left-32 w-32 h-32 bg-white/8 rounded-3xl blur-xl animate-float-slow rotate-45"></div>
        <div className="absolute top-48 right-40 w-24 h-24 bg-white/6 rounded-2xl blur-xl animate-float-slow" style={{animationDelay: '2.5s'}}></div>
        <div className="absolute bottom-40 left-40 w-40 h-40 bg-white/5 rounded-3xl blur-xl animate-float-slow" style={{animationDelay: '1.8s'}}></div>
        <div className="absolute bottom-32 right-32 w-20 h-20 bg-white/7 rounded-2xl blur-xl animate-float-slow" style={{animationDelay: '0.7s'}}></div>

        {/* Additional floating elements */}
        <div className="absolute top-1/3 left-1/3 w-16 h-16 bg-white/4 rounded-full blur-lg animate-float-slow" style={{animationDelay: '1.2s'}}></div>
        <div className="absolute bottom-1/3 right-1/3 w-24 h-24 bg-white/3 rounded-full blur-lg animate-float-slow" style={{animationDelay: '2.8s'}}></div>
      </div>

      {/* Enhanced Scroll Indicator */}
      {fullScreen && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow group cursor-pointer">
          <div className="w-8 h-12 border-2 border-white/60 rounded-full flex justify-center group-hover:border-white transition-colors duration-300">
            <div className="w-1 h-4 bg-white/80 rounded-full mt-2 animate-pulse group-hover:bg-white transition-colors duration-300"></div>
          </div>
          <div className="text-white/60 text-sm mt-2 font-medium group-hover:text-white transition-colors duration-300">
            Scroll
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
