import React, { createContext, useContext, useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

// Theme color definitions
export const THEME_COLORS = {
  cinematic: {
    primary: '#5e716a',
    secondary: '#64748B',
    tertiary: '#8da59c',
    light: '#f0f3f2',
    dark: '#38433e',
    accent: '#1eba55',
    background: '#ffffff',
    surface: '#f8fafc',
    text: '#1f2937',
    textLight: '#6b7280',
    border: '#e5e7eb',
    shadow: 'rgba(94, 113, 106, 0.1)',
    gradient: {
      from: '#5e716a',
      via: '#67877b',
      to: '#8da59c'
    },
    cinematicGradient: 'linear-gradient(-45deg, #5e716a, #67877b, #8da59c, #5e716a, #4b5a54)',
    floatingElements: {
      primary: 'rgba(94, 113, 106, 0.1)',
      secondary: 'rgba(100, 116, 139, 0.1)',
      accent: 'rgba(30, 186, 85, 0.1)'
    }
  },
  logistics: {
    primary: '#bf2530',
    secondary: '#dc2626',
    tertiary: '#f87171',
    light: '#fef2f2',
    dark: '#991b1b',
    accent: '#fca5a5',
    background: '#ffffff',
    surface: '#fefefe',
    text: '#1f2937',
    textLight: '#6b7280',
    border: '#fecaca',
    shadow: 'rgba(191, 37, 48, 0.1)',
    gradient: {
      from: '#bf2530',
      via: '#f87171',
      to: '#fca5a5'
    },
    cinematicGradient: 'linear-gradient(-45deg, #bf2530, #f87171, #fca5a5, #bf2530, #dc2626)',
    floatingElements: {
      primary: 'rgba(191, 37, 48, 0.1)',
      secondary: 'rgba(220, 38, 38, 0.1)',
      accent: 'rgba(252, 165, 165, 0.1)'
    }
  },
  travel: {
    primary: '#1eba55',
    secondary: '#16a34a',
    tertiary: '#4ade80',
    light: '#f0fdf4',
    dark: '#15803d',
    accent: '#86efac',
    background: '#ffffff',
    surface: '#fafafa',
    text: '#1f2937',
    textLight: '#6b7280',
    border: '#bbf7d0',
    shadow: 'rgba(30, 186, 85, 0.1)',
    gradient: {
      from: '#1eba55',
      via: '#4ade80',
      to: '#86efac'
    },
    cinematicGradient: 'linear-gradient(-45deg, #1eba55, #4ade80, #86efac, #1eba55, #16a34a)',
    floatingElements: {
      primary: 'rgba(30, 186, 85, 0.1)',
      secondary: 'rgba(22, 163, 74, 0.1)',
      accent: 'rgba(134, 239, 172, 0.1)'
    }
  }
};

// Theme context
const ThemeContext = createContext();

// Theme provider component
export const ThemeProvider = ({ children }) => {
  const location = useLocation();
  const [currentTheme, setCurrentTheme] = useState('cinematic');

  // Auto-detect theme based on current route
  useEffect(() => {
    const path = location.pathname;
    if (path.startsWith('/logistics')) {
      setCurrentTheme('logistics');
    } else if (path.startsWith('/travel')) {
      setCurrentTheme('travel');
    } else {
      setCurrentTheme('cinematic');
    }
  }, [location.pathname]);

  // Get current theme colors
  const getThemeColors = () => THEME_COLORS[currentTheme];

  // Get CSS custom properties for the current theme
  const getThemeCSSProperties = () => {
    const colors = getThemeColors();
    return {
      '--theme-primary': colors.primary,
      '--theme-secondary': colors.secondary,
      '--theme-tertiary': colors.tertiary,
      '--theme-light': colors.light,
      '--theme-dark': colors.dark,
      '--theme-accent': colors.accent,
      '--theme-background': colors.background,
      '--theme-surface': colors.surface,
      '--theme-text': colors.text,
      '--theme-text-light': colors.textLight,
      '--theme-border': colors.border,
      '--theme-shadow': colors.shadow,
      '--theme-gradient-from': colors.gradient.from,
      '--theme-gradient-via': colors.gradient.via,
      '--theme-gradient-to': colors.gradient.to,
      '--theme-cinematic-gradient': colors.cinematicGradient,
      '--theme-floating-primary': colors.floatingElements.primary,
      '--theme-floating-secondary': colors.floatingElements.secondary,
      '--theme-floating-accent': colors.floatingElements.accent
    };
  };

  // Apply theme to document root
  useEffect(() => {
    const root = document.documentElement;
    const cssProperties = getThemeCSSProperties();

    Object.entries(cssProperties).forEach(([property, value]) => {
      root.style.setProperty(property, value);
    });

    // Add theme class to body for additional styling
    document.body.className = document.body.className.replace(/theme-\w+/g, '');
    document.body.classList.add(`theme-${currentTheme}`);
  }, [currentTheme]);

  const value = {
    currentTheme,
    setCurrentTheme,
    getThemeColors,
    getThemeCSSProperties,
    THEME_COLORS
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};

// Custom hook to use theme context
export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export default ThemeContext;
