import React from 'react';

const TestimonialCard = ({ 
  name, 
  position, 
  company, 
  content, 
  rating = 5,
  avatar = null,
  accentColor = 'primary'
}) => {
  const accentColors = {
    primary: 'border-primary-200/50 bg-gradient-to-br from-primary-50/50 to-primary-100/30',
    logistics: 'border-logistics-200/50 bg-gradient-to-br from-logistics-50/50 to-logistics-100/30',
    travel: 'border-travel-200/50 bg-gradient-to-br from-travel-50/50 to-travel-100/30'
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <svg
        key={i}
        className={`w-5 h-5 ${i < rating ? 'text-yellow-400' : 'text-gray-300'}`}
        fill="currentColor"
        viewBox="0 0 20 20"
      >
        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
      </svg>
    ));
  };

  return (
    <div className={`group relative p-8 rounded-3xl border border-white/40 shadow-elevated hover:shadow-floating transition-all duration-500 cursor-pointer overflow-hidden hover:-translate-y-2 hover:scale-105 ${accentColors[accentColor]}`}>
      {/* Quote Icon */}
      <div className="absolute top-6 right-6 opacity-10 group-hover:opacity-20 transition-opacity duration-300">
        <svg className="w-12 h-12 text-gray-600" fill="currentColor" viewBox="0 0 24 24">
          <path d="M14.017 21v-7.391c0-5.704 3.731-9.57 8.983-10.609l.995 2.151c-2.432.917-3.995 3.638-3.995 5.849h4v10h-9.983zm-14.017 0v-7.391c0-5.704 3.748-9.57 9-10.609l.996 2.151c-2.433.917-3.996 3.638-3.996 5.849h4v10h-10z"/>
        </svg>
      </div>

      {/* Content */}
      <div className="relative z-10">
        {/* Rating */}
        <div className="flex items-center mb-4">
          {renderStars(rating)}
        </div>

        {/* Testimonial Text */}
        <blockquote className="text-lg text-gray-700 mb-6 leading-relaxed italic">
          "{content}"
        </blockquote>

        {/* Author */}
        <div className="flex items-center">
          {avatar ? (
            <img 
              src={avatar} 
              alt={name}
              className="w-12 h-12 rounded-full object-cover mr-4 shadow-lg"
            />
          ) : (
            <div className="w-12 h-12 rounded-full bg-gradient-to-br from-primary-500 to-primary-600 flex items-center justify-center text-white font-bold text-lg mr-4 shadow-lg">
              {name.charAt(0)}
            </div>
          )}
          <div>
            <div className="font-bold text-gray-900">{name}</div>
            <div className="text-sm text-gray-600">{position}</div>
            <div className="text-sm text-primary-600 font-medium">{company}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TestimonialCard;
