import React, { createContext, useContext, useState } from 'react';

const FooterContext = createContext();

export const useFooter = () => {
  const context = useContext(FooterContext);
  if (!context) {
    throw new Error('useFooter must be used within a FooterProvider');
  }
  return context;
};

export const FooterProvider = ({ children }) => {
  const [footerType, setFooterType] = useState('default'); // 'default', 'logistics', 'travel'

  const setLogisticsFooter = () => setFooterType('logistics');
  const setTravelFooter = () => setFooterType('travel');
  const setDefaultFooter = () => setFooterType('default');

  const value = {
    footerType,
    setLogisticsFooter,
    setTravelFooter,
    setDefaultFooter
  };

  return (
    <FooterContext.Provider value={value}>
      {children}
    </FooterContext.Provider>
  );
};
