import React, { useState, useEffect } from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  description,
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true,
  showStats = false,
  stats = []
}) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Enhanced Dynamic Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Interactive floating orbs that follow mouse */}
        <div 
          className="absolute w-96 h-96 rounded-full blur-3xl animate-float-slow transition-all duration-1000 ease-out"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.08)',
            left: `${mousePosition.x * 0.3}%`,
            top: `${mousePosition.y * 0.2}%`,
            transform: 'translate(-50%, -50%)'
          }}
        ></div>
        <div 
          className="absolute w-80 h-80 rounded-full blur-3xl animate-float-slow transition-all duration-1000 ease-out"
          style={{
            backgroundColor: 'rgba(255, 255, 255, 0.06)',
            left: `${100 - mousePosition.x * 0.4}%`,
            top: `${100 - mousePosition.y * 0.3}%`,
            transform: 'translate(50%, 50%)',
            animationDelay: '2s'
          }}
        ></div>

        {/* Animated gradient mesh */}
        <div className="absolute inset-0 opacity-30">
          <div className="absolute inset-0 bg-gradient-to-br from-primary-500/20 via-transparent to-travel-500/20 animate-pulse-slow"></div>
          <div className="absolute inset-0 bg-gradient-to-tl from-logistics-500/15 via-transparent to-primary-500/15 animate-pulse-slow" style={{animationDelay: '1s'}}></div>
        </div>
      </div>

      {/* Enhanced Premium Overlay */}
      <div className="absolute inset-0 bg-cinematic-overlay opacity-25"></div>

      {/* Animated Grid Pattern */}
      <div className="absolute inset-0 bg-grid-pattern opacity-8 animate-pulse-slow"></div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          {/* Enhanced Title with Better Typography */}
          <div className={`mb-8 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`}>
            <h1 className={`text-7xl md:text-8xl lg:text-9xl font-black mb-6 ${textColor} text-shadow-lg tracking-tight leading-none`}>
              <span className="block transform transition-all duration-1000 hover:scale-105 hover:text-primary-200">
                {title.split(' ')[0]}
              </span>
              <span className="block transform transition-all duration-1000 hover:scale-105 hover:text-travel-200">
                {title.split(' ')[1] || ''}
              </span>
            </h1>
          </div>

          {/* Enhanced Subtitle */}
          {subtitle && (
            <div className={`mb-8 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.2s'}}>
              <p className={`text-3xl md:text-4xl lg:text-5xl font-bold ${subtitleColor} leading-tight tracking-wide`}>
                <span className="inline-block transform transition-all duration-500 hover:scale-105 hover:text-white">
                  {subtitle}
                </span>
              </p>
            </div>
          )}

          {/* Enhanced Description */}
          {description && (
            <div className={`mb-12 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.4s'}}>
              <p className={`text-xl md:text-2xl lg:text-3xl max-w-6xl mx-auto ${subtitleColor} leading-relaxed font-light`}>
                {description}
              </p>
            </div>
          )}

          {/* Enhanced CTA Buttons */}
          {ctaButtons.length > 0 && (
            <div className={`flex flex-col sm:flex-row gap-8 justify-center items-center mb-16 ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.6s'}}>
              {ctaButtons.map((button, index) => {
                // If button has custom className, render as direct button element
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover group relative overflow-hidden`}
                    >
                      <span className="relative z-10 flex items-center gap-3">
                        {button.text}
                        <svg className="w-5 h-5 transform group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                        </svg>
                      </span>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover group"
                  >
                    <span className="flex items-center gap-3">
                      {button.text}
                      <svg className="w-5 h-5 transform group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                      </svg>
                    </span>
                  </Button>
                );
              })}
            </div>
          )}

          {/* Enhanced Stats Section */}
          {showStats && stats.length > 0 && (
            <div className={`grid grid-cols-2 md:grid-cols-4 gap-8 max-w-6xl mx-auto ${isLoaded ? 'animate-fade-in-up' : 'opacity-0'}`} style={{animationDelay: '0.8s'}}>
              {stats.map((stat, index) => (
                <div key={index} className="text-center group">
                  <div className="text-4xl md:text-5xl font-black text-white mb-2 group-hover:scale-110 transition-transform duration-300">
                    {stat.value}
                  </div>
                  <div className="text-lg text-gray-200 font-medium group-hover:text-white transition-colors duration-300">
                    {stat.label}
                  </div>
                </div>
              ))}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Enhanced Premium Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating geometric shapes with enhanced animations */}
        <div className="absolute top-32 left-32 w-32 h-32 bg-white/10 rounded-3xl blur-xl animate-float transform rotate-12"></div>
        <div className="absolute top-48 right-40 w-24 h-24 bg-white/8 rounded-2xl blur-xl animate-float transform -rotate-12" style={{animationDelay: '2.5s'}}></div>
        <div className="absolute bottom-40 left-40 w-36 h-36 bg-white/12 rounded-3xl blur-xl animate-float transform rotate-45" style={{animationDelay: '1.8s'}}></div>
        <div className="absolute bottom-32 right-32 w-20 h-20 bg-white/6 rounded-2xl blur-xl animate-float transform -rotate-45" style={{animationDelay: '0.7s'}}></div>

        {/* Additional floating elements */}
        <div className="absolute top-1/4 left-1/4 w-16 h-16 bg-white/5 rounded-full blur-lg animate-float" style={{animationDelay: '3s'}}></div>
        <div className="absolute top-3/4 right-1/4 w-12 h-12 bg-white/7 rounded-full blur-lg animate-float" style={{animationDelay: '4s'}}></div>

        {/* Dots pattern with animation */}
        <div className="absolute inset-0 bg-dots-pattern opacity-8 animate-pulse-slow"></div>
      </div>

      {/* Enhanced Scroll Indicator */}
      {fullScreen && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow group cursor-pointer">
          <div className="w-8 h-12 border-2 border-white/60 rounded-full flex justify-center group-hover:border-white transition-colors duration-300">
            <div className="w-1.5 h-4 bg-white/80 rounded-full mt-2 animate-pulse group-hover:bg-white transition-colors duration-300"></div>
          </div>
          <div className="text-white/60 text-sm mt-2 font-medium group-hover:text-white transition-colors duration-300">
            Scroll
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
