import React, { useState } from 'react';

const FAQ = ({ faqs = [], className = '' }) => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {faqs.map((faq, index) => (
        <div 
          key={index}
          className="group relative bg-white/95 backdrop-blur-xl rounded-2xl border border-white/40 shadow-elevated hover:shadow-floating transition-all duration-500 overflow-hidden"
        >
          {/* Gradient overlay on hover */}
          <div className="absolute inset-0 bg-gradient-to-br from-primary-50/30 via-transparent to-travel-50/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

          <button
            onClick={() => toggleFAQ(index)}
            className="w-full px-8 py-6 text-left focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 rounded-2xl transition-all duration-300"
          >
            <div className="flex items-center justify-between">
              <h3 className="text-xl font-bold text-gray-900 group-hover:text-gray-800 transition-colors duration-300 pr-4">
                {faq.question}
              </h3>
              <div className={`flex-shrink-0 w-8 h-8 bg-gradient-to-br from-primary-500 to-primary-600 rounded-xl flex items-center justify-center text-white transition-all duration-300 ${openIndex === index ? 'rotate-180' : ''}`}>
                <svg 
                  className="w-5 h-5 transition-transform duration-300" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M19 9l-7 7-7-7" 
                  />
                </svg>
              </div>
            </div>
          </button>

          <div className={`overflow-hidden transition-all duration-500 ease-in-out ${
            openIndex === index ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
          }`}>
            <div className="px-8 pb-6">
              <div className="border-t border-gray-100 pt-4">
                <p className="text-gray-600 leading-relaxed text-lg">
                  {faq.answer}
                </p>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FAQ;
