import React from 'react';
import { useLocation, useSearchParams } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import GeneralFooter from './GeneralFooter';
import LogisticsFooter from './LogisticsFooter';
import TravelFooter from './TravelFooter';
import Breadcrumb from '../common/Breadcrumb';
import ScrollToTop from '../common/ScrollToTop';
import { useFooter } from '../../contexts/FooterContext';

const Layout = ({ children }) => {
  const location = useLocation();
  const [searchParams] = useSearchParams();
  const { footerType } = useFooter();
  const isLogisticsPage = location.pathname === '/logistics';
  const isTravelPage = location.pathname === '/travel';
  const isContactPage = location.pathname === '/contact';
  const isLogisticsContact = isContactPage && (searchParams.get('category') === 'logistics' || footerType === 'logistics');
  const isTravelContact = isContactPage && (searchParams.get('category') === 'travel' || footerType === 'travel');
  const isGeneralContact = isContactPage && (searchParams.get('category') === 'general' || footerType === 'general' || (!searchParams.get('category') && footerType === 'general'));

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <Breadcrumb />
      <main className="flex-grow">
        {children}
      </main>
      {/* Conditional Footer Rendering - Only One Footer */}
      {(isLogisticsPage || isLogisticsContact) && <LogisticsFooter />}
      {(isTravelPage || isTravelContact) && !(isLogisticsPage || isLogisticsContact) && <TravelFooter />}
      {(!isLogisticsPage && !isTravelPage && !isContactPage) && <GeneralFooter />}
      {isGeneralContact && <GeneralFooter />}
      <ScrollToTop />
    </div>
  );
};

export default Layout;
