import React from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true
}) => {
  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  // Dynamic background colors based on theme
  const getBackgroundColors = () => {
    switch(background) {
      case 'logistics':
        return {
          primary: '#bf2530',
          secondary: '#dc2626',
          tertiary: '#f87171',
          light: '#fee2e2',
          dark: '#991b1b'
        };
      case 'travel':
        return {
          primary: '#1eba55',
          secondary: '#16a34a',
          tertiary: '#4ade80',
          light: '#dcfce7',
          dark: '#15803d'
        };
      default:
        return {
          primary: '#5e716a',
          secondary: '#4b5a54',
          tertiary: '#8da59c',
          light: '#f0f3f2',
          dark: '#38433e'
        };
    }
  };

  const colors = getBackgroundColors();

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Premium Glassmorphism Background System */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Base gradient layer */}
        <div 
          className="absolute inset-0"
          style={{
            background: `linear-gradient(135deg, ${colors.primary}15 0%, ${colors.secondary}10 25%, ${colors.tertiary}12 50%, ${colors.primary}8 75%, ${colors.secondary}10 100%)`
          }}
        ></div>

        {/* Large glassmorphism orbs */}
        <div 
          className="absolute -top-32 -right-32 w-96 h-96 rounded-full backdrop-blur-3xl animate-float-slow opacity-20"
          style={{ 
            backgroundColor: `${colors.tertiary}15`,
            border: `1px solid ${colors.tertiary}20`,
            boxShadow: `0 8px 32px ${colors.tertiary}10`
          }}
        ></div>
        <div 
          className="absolute -bottom-32 -left-32 w-80 h-80 rounded-full backdrop-blur-3xl animate-float-slow opacity-25"
          style={{ 
            backgroundColor: `${colors.secondary}18`,
            border: `1px solid ${colors.secondary}25`,
            boxShadow: `0 8px 32px ${colors.secondary}12`,
            animationDelay: '3s'
          }}
        ></div>

        {/* Medium glassmorphism elements */}
        <div 
          className="absolute top-1/4 left-1/4 w-64 h-64 rounded-full backdrop-blur-2xl animate-float-slow opacity-15"
          style={{ 
            backgroundColor: `${colors.primary}12`,
            border: `1px solid ${colors.primary}18`,
            boxShadow: `0 6px 24px ${colors.primary}8`,
            animationDelay: '2s'
          }}
        ></div>
        <div 
          className="absolute bottom-1/3 right-1/3 w-48 h-48 rounded-full backdrop-blur-2xl animate-float-slow opacity-20"
          style={{ 
            backgroundColor: `${colors.light}10`,
            border: `1px solid ${colors.light}15`,
            boxShadow: `0 6px 24px ${colors.light}6`,
            animationDelay: '4s'
          }}
        ></div>

        {/* Small accent glassmorphism elements */}
        <div 
          className="absolute top-1/2 right-1/4 w-32 h-32 rounded-2xl backdrop-blur-xl animate-float-slow opacity-12"
          style={{ 
            backgroundColor: `${colors.tertiary}8`,
            border: `1px solid ${colors.tertiary}12`,
            boxShadow: `0 4px 16px ${colors.tertiary}6`,
            animationDelay: '1.5s'
          }}
        ></div>
        <div 
          className="absolute bottom-1/4 left-1/3 w-40 h-40 rounded-2xl backdrop-blur-xl animate-float-slow opacity-15"
          style={{ 
            backgroundColor: `${colors.secondary}10`,
            border: `1px solid ${colors.secondary}15`,
            boxShadow: `0 4px 16px ${colors.secondary}8`,
            animationDelay: '3.5s'
          }}
        ></div>
      </div>

      {/* Glassmorphism overlay with enhanced blur */}
      <div 
        className="absolute inset-0 backdrop-blur-sm opacity-20"
        style={{
          background: `linear-gradient(135deg, ${colors.primary}08 0%, ${colors.secondary}06 50%, ${colors.tertiary}08 100%)`,
          border: `1px solid ${colors.primary}10`
        }}
      ></div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          <h1 className={`text-6xl md:text-8xl lg:text-9xl font-black mb-12 ${textColor} animate-fade-in-up text-shadow-lg tracking-tight`}>
            {title}
          </h1>
          {subtitle && (
            <p className={`text-2xl md:text-3xl lg:text-4xl mb-16 max-w-5xl mx-auto ${subtitleColor} animate-fade-in-up leading-relaxed font-light`} style={{animationDelay: '0.3s'}}>
              {subtitle}
            </p>
          )}

          {ctaButtons.length > 0 && (
            <div className="flex flex-col sm:flex-row gap-8 justify-center items-center mb-16 animate-fade-in-up" style={{animationDelay: '0.6s'}}>
              {ctaButtons.map((button, index) => {
                // If button has custom className, render as direct button element
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover`}
                    >
                      <span className="relative z-10 flex items-center gap-2">{button.text}</span>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover"
                  >
                    {button.text}
                  </Button>
                );
              })}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Premium Glassmorphism Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating glassmorphism cards */}
        <div 
          className="absolute top-1/3 left-1/5 w-24 h-24 rounded-3xl backdrop-blur-xl animate-float-slow opacity-15"
          style={{ 
            backgroundColor: `${colors.light}12`,
            border: `1px solid ${colors.light}20`,
            boxShadow: `0 8px 32px ${colors.light}8`,
            animationDelay: '2s'
          }}
        ></div>
        <div 
          className="absolute bottom-1/3 right-1/5 w-20 h-20 rounded-3xl backdrop-blur-xl animate-float-slow opacity-18"
          style={{ 
            backgroundColor: `${colors.tertiary}10`,
            border: `1px solid ${colors.tertiary}18`,
            boxShadow: `0 8px 32px ${colors.tertiary}6`,
            animationDelay: '3.5s'
          }}
        ></div>

        {/* Subtle glassmorphism pattern overlay */}
        <div 
          className="absolute inset-0 backdrop-blur-xs opacity-5"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='80' height='80' viewBox='0 0 80 80' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='${colors.primary.replace('#', '%23')}' fill-opacity='0.02'%3E%3Ccircle cx='40' cy='40' r='1.5'/%3E%3C/g%3E%3C/svg%3E")`,
            border: `1px solid ${colors.primary}05`
          }}
        ></div>

        {/* Additional glassmorphism accents */}
        <div 
          className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-16 h-16 rounded-full backdrop-blur-lg animate-pulse-slow opacity-10"
          style={{ 
            backgroundColor: `${colors.secondary}08`,
            border: `1px solid ${colors.secondary}12`,
            boxShadow: `0 4px 16px ${colors.secondary}4`
          }}
        ></div>
      </div>

      {/* Enhanced Scroll Indicator */}
      {fullScreen && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow">
          <div 
            className="w-6 h-10 border-2 rounded-full flex justify-center backdrop-blur-sm"
            style={{ 
              borderColor: `${colors.light}80`,
              backgroundColor: `${colors.primary}20`
            }}
          >
            <div 
              className="w-1 h-3 rounded-full mt-2 animate-pulse"
              style={{ backgroundColor: colors.light }}
            ></div>
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
