import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Button from './Button';

const PremiumHero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setIsLoaded(true);
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % 3);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const heroSlides = [
    {
      title: "Global Excellence",
      subtitle: "Connecting continents through innovative logistics solutions",
      description: "From freight forwarding to supply chain optimization, we deliver world-class logistics services that keep your business moving forward.",
      cta: "Explore Logistics",
      link: "/logistics",
      variant: "logistics",
      gradient: "from-logistics-500 via-logistics-600 to-logistics-700",
      accent: "#bf2530"
    },
    {
      title: "Seamless Journeys",
      subtitle: "Transforming travel experiences worldwide",
      description: "Whether it's medical travel, educational opportunities, or corporate journeys, we create seamless experiences that exceed expectations.",
      cta: "Discover Travel",
      link: "/travel", 
      variant: "travel",
      gradient: "from-travel-500 via-travel-600 to-travel-700",
      accent: "#1eba55"
    },
    {
      title: "Unified Vision",
      subtitle: "Empowering businesses across borders",
      description: "Skygate Group combines logistics expertise with travel excellence to deliver comprehensive solutions that drive your success.",
      cta: "Learn More",
      link: "/about",
      variant: "primary",
      gradient: "from-primary-500 via-primary-600 to-primary-700",
      accent: "#5e716a"
    }
  ];

  const currentSlideData = heroSlides[currentSlide];

  return (
    <section className="relative min-h-screen flex items-center justify-center overflow-hidden bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900">
      {/* Dynamic Background Layers */}
      <div className="absolute inset-0">
        {/* Animated Gradient Overlay */}
        <div 
          className={`absolute inset-0 bg-gradient-to-br ${currentSlideData.gradient} opacity-20 transition-all duration-1000 ease-in-out`}
          style={{
            background: `linear-gradient(135deg, ${currentSlideData.accent}20, ${currentSlideData.accent}10, transparent)`
          }}
        />

        {/* Floating Geometric Shapes */}
        <div className="absolute inset-0 overflow-hidden">
          <div className="absolute top-20 left-20 w-32 h-32 bg-white/5 rounded-full blur-2xl animate-pulse" />
          <div className="absolute top-40 right-32 w-24 h-24 bg-white/8 rounded-full blur-xl animate-bounce" style={{ animationDelay: '1s' }} />
          <div className="absolute bottom-32 left-40 w-40 h-40 bg-white/6 rounded-full blur-2xl animate-pulse" style={{ animationDelay: '2s' }} />
          <div className="absolute bottom-20 right-20 w-28 h-28 bg-white/7 rounded-full blur-xl animate-bounce" style={{ animationDelay: '3s' }} />
        </div>

        {/* Grid Pattern Overlay */}
        <div 
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage: `radial-gradient(circle at 1px 1px, ${currentSlideData.accent} 1px, transparent 0)`,
            backgroundSize: '40px 40px'
          }}
        />
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <div className={`transition-all duration-1000 ${isLoaded ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-10'}`}>

          {/* Brand Badge */}
          <div className="inline-flex items-center px-6 py-3 mb-8 bg-white/10 backdrop-blur-xl rounded-full border border-white/20 shadow-2xl">
            <div className="w-3 h-3 bg-green-400 rounded-full mr-3 animate-pulse" />
            <span className="text-white/90 font-semibold text-sm tracking-wide uppercase">
              Trusted Since 2018
            </span>
          </div>

          {/* Main Headline */}
          <h1 className="text-5xl sm:text-6xl md:text-7xl lg:text-8xl font-black text-white mb-6 leading-tight">
            <span className="block mb-2">Skygate</span>
            <span 
              className="block bg-gradient-to-r from-white via-white to-gray-300 bg-clip-text text-transparent"
              style={{
                background: `linear-gradient(135deg, white, ${currentSlideData.accent}, white)`,
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}
            >
              {currentSlideData.title}
            </span>
          </h1>

          {/* Subtitle */}
          <p className="text-xl sm:text-2xl md:text-3xl text-gray-300 mb-8 max-w-4xl mx-auto leading-relaxed font-light">
            {currentSlideData.subtitle}
          </p>

          {/* Description */}
          <p className="text-lg sm:text-xl text-gray-400 mb-12 max-w-3xl mx-auto leading-relaxed">
            {currentSlideData.description}
          </p>

          {/* CTA Buttons */}
          <div className="flex flex-col sm:flex-row gap-6 justify-center items-center mb-16">
            <Link to={currentSlideData.link}>
              <Button 
                variant={currentSlideData.variant} 
                size="xl"
                className="group relative overflow-hidden shadow-2xl hover:shadow-3xl transform hover:-translate-y-2 transition-all duration-500"
              >
                <span className="relative z-10 flex items-center gap-3">
                  {currentSlideData.cta}
                  <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                  </svg>
                </span>
                <div 
                  className="absolute inset-0 bg-gradient-to-r from-white/20 to-transparent transform -skew-x-12 -translate-x-full group-hover:translate-x-full transition-transform duration-1000"
                />
              </Button>
            </Link>

            <Link to="/contact">
              <Button 
                variant="outline" 
                size="xl"
                className="border-2 border-white/30 text-white hover:bg-white hover:text-gray-900 backdrop-blur-xl bg-white/10 shadow-xl hover:shadow-2xl transform hover:-translate-y-2 transition-all duration-500"
              >
                Get Started Today
              </Button>
            </Link>
          </div>

          {/* Trust Indicators */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-8 max-w-4xl mx-auto">
            <div className="text-center group">
              <div className="text-3xl sm:text-4xl font-bold text-white mb-2 group-hover:text-green-400 transition-colors duration-300">
                500+
              </div>
              <div className="text-gray-400 text-sm uppercase tracking-wide">
                Global Clients
              </div>
            </div>
            <div className="text-center group">
              <div className="text-3xl sm:text-4xl font-bold text-white mb-2 group-hover:text-blue-400 transition-colors duration-300">
                50+
              </div>
              <div className="text-gray-400 text-sm uppercase tracking-wide">
                Countries Served
              </div>
            </div>
            <div className="text-center group">
              <div className="text-3xl sm:text-4xl font-bold text-white mb-2 group-hover:text-purple-400 transition-colors duration-300">
                7+
              </div>
              <div className="text-gray-400 text-sm uppercase tracking-wide">
                Years Excellence
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Slide Indicators */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-3">
        {heroSlides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrentSlide(index)}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              index === currentSlide 
                ? 'bg-white shadow-lg scale-125' 
                : 'bg-white/30 hover:bg-white/50'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>

      {/* Scroll Indicator */}
      <div className="absolute bottom-8 right-8 animate-bounce">
        <div className="flex flex-col items-center text-white/60 hover:text-white transition-colors duration-300 cursor-pointer">
          <span className="text-xs uppercase tracking-wide mb-2">Scroll</span>
          <div className="w-6 h-10 border-2 border-white/40 rounded-full flex justify-center">
            <div className="w-1 h-3 bg-white/60 rounded-full mt-2 animate-pulse" />
          </div>
        </div>
      </div>

      {/* Floating Action Elements */}
      <div className="absolute top-1/2 left-8 transform -translate-y-1/2 hidden lg:block">
        <div className="flex flex-col space-y-4">
          <Link 
            to="/logistics"
            className="group p-4 bg-white/10 backdrop-blur-xl rounded-2xl border border-white/20 hover:bg-white/20 transition-all duration-300 hover:scale-110"
          >
            <div className="w-8 h-8 bg-red-500 rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform duration-300">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7h12m0 0l-4-4m4 4l-4 4m0 6H4m0 0l4 4m-4-4l4-4" />
              </svg>
            </div>
          </Link>

          <Link 
            to="/travel"
            className="group p-4 bg-white/10 backdrop-blur-xl rounded-2xl border border-white/20 hover:bg-white/20 transition-all duration-300 hover:scale-110"
          >
            <div className="w-8 h-8 bg-green-500 rounded-lg flex items-center justify-center group-hover:scale-110 transition-transform duration-300">
              <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
              </svg>
            </div>
          </Link>
        </div>
      </div>

      {/* Right Side Floating Elements */}
      <div className="absolute top-1/2 right-8 transform -translate-y-1/2 hidden lg:block">
        <div className="text-right">
          <div className="p-6 bg-white/5 backdrop-blur-xl rounded-3xl border border-white/10">
            <div className="text-white/80 text-sm mb-2">Next Service</div>
            <div className="text-white font-bold text-lg">
              {currentSlide === 0 ? 'Travel Solutions' : currentSlide === 1 ? 'Logistics Excellence' : 'Global Network'}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default PremiumHero;
