import React from 'react';
import Button from './Button';

const HeroSection = ({ 
  title, 
  subtitle, 
  background = 'cinematic', 
  ctaButtons = [],
  className = '',
  children,
  fullScreen = true
}) => {
  const backgrounds = {
    cinematic: 'gradient-cinematic relative overflow-hidden',
    logistics: 'gradient-logistics-cinematic relative overflow-hidden',
    travel: 'gradient-travel-cinematic relative overflow-hidden',
    white: 'bg-white',
    gray: 'bg-gray-50',
    premium: 'bg-premium-pattern relative overflow-hidden'
  };

  const textColor = background === 'white' || background === 'gray' ? 'text-gray-900' : 'text-white';
  const subtitleColor = background === 'white' || background === 'gray' ? 'text-gray-600' : 'text-gray-100';

  // Dynamic background colors based on theme
  const getBackgroundColors = () => {
    switch(background) {
      case 'logistics':
        return {
          primary: '#bf2530',
          secondary: '#dc2626',
          tertiary: '#f87171',
          light: '#fee2e2',
          dark: '#991b1b'
        };
      case 'travel':
        return {
          primary: '#1eba55',
          secondary: '#16a34a',
          tertiary: '#4ade80',
          light: '#dcfce7',
          dark: '#15803d'
        };
      default:
        return {
          primary: '#5e716a',
          secondary: '#4b5a54',
          tertiary: '#8da59c',
          light: '#f0f3f2',
          dark: '#38433e'
        };
    }
  };

  const colors = getBackgroundColors();

  return (
    <section className={`${fullScreen ? 'hero-premium' : 'relative py-32 px-4'} ${backgrounds[background]} ${className}`}>
      {/* Simplified Dynamic Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Just 2 main floating orbs for subtle effect */}
        <div 
          className="absolute -top-40 -right-40 w-80 h-80 rounded-full blur-3xl animate-float-slow opacity-15"
          style={{ backgroundColor: colors.tertiary }}
        ></div>
        <div 
          className="absolute -bottom-40 -left-40 w-72 h-72 rounded-full blur-3xl animate-float-slow opacity-18"
          style={{ backgroundColor: colors.secondary, animationDelay: '3s' }}
        ></div>
      </div>

      {/* Subtle Premium Overlay */}
      <div 
        className="absolute inset-0 opacity-15"
        style={{
          background: `linear-gradient(135deg, ${colors.primary}20 0%, ${colors.secondary}15 50%, ${colors.tertiary}20 100%)`
        }}
      ></div>

      <div className="container-premium relative z-10">
        <div className="text-center">
          <h1 className={`text-6xl md:text-8xl lg:text-9xl font-black mb-12 ${textColor} animate-fade-in-up text-shadow-lg tracking-tight`}>
            {title}
          </h1>
          {subtitle && (
            <p className={`text-2xl md:text-3xl lg:text-4xl mb-16 max-w-5xl mx-auto ${subtitleColor} animate-fade-in-up leading-relaxed font-light`} style={{animationDelay: '0.3s'}}>
              {subtitle}
            </p>
          )}

          {ctaButtons.length > 0 && (
            <div className="flex flex-col sm:flex-row gap-8 justify-center items-center mb-16 animate-fade-in-up" style={{animationDelay: '0.6s'}}>
              {ctaButtons.map((button, index) => {
                // If button has custom className, render as direct button element
                if (button.className) {
                  return (
                    <button
                      key={index}
                      onClick={button.onClick}
                      className={`${button.className} shadow-glow-xl hover:shadow-glow-xl interactive-hover`}
                    >
                      <span className="relative z-10 flex items-center gap-2">{button.text}</span>
                    </button>
                  );
                }
                // Otherwise use Button component
                return (
                  <Button
                    key={index}
                    variant={button.variant || 'primary'}
                    size={button.size || 'xl'}
                    onClick={button.onClick}
                    className="shadow-glow-xl hover:shadow-glow-xl interactive-hover"
                  >
                    {button.text}
                  </Button>
                );
              })}
            </div>
          )}

          {children}
        </div>
      </div>

      {/* Simplified Premium Decorative Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Just one subtle geometric shape */}
        <div 
          className="absolute top-1/4 right-1/4 w-16 h-16 rounded-2xl blur-xl animate-float-slow opacity-8"
          style={{ backgroundColor: colors.light, animationDelay: '2s' }}
        ></div>

        {/* Simple pattern overlay - much more subtle */}
        <div 
          className="absolute inset-0 opacity-3"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='${colors.primary.replace('#', '%23')}' fill-opacity='0.03'%3E%3Ccircle cx='30' cy='30' r='1'/%3E%3C/g%3E%3C/svg%3E")`
          }}
        ></div>
      </div>

      {/* Enhanced Scroll Indicator */}
      {fullScreen && (
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce-slow">
          <div 
            className="w-6 h-10 border-2 rounded-full flex justify-center backdrop-blur-sm"
            style={{ 
              borderColor: `${colors.light}80`,
              backgroundColor: `${colors.primary}20`
            }}
          >
            <div 
              className="w-1 h-3 rounded-full mt-2 animate-pulse"
              style={{ backgroundColor: colors.light }}
            ></div>
          </div>
        </div>
      )}
    </section>
  );
};

export default HeroSection;
