import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Breadcrumbs = ({ className = '' }) => {
  const location = useLocation();

  const pathSegments = location.pathname.split('/').filter(segment => segment !== '');

  const getPageTitle = (path) => {
    const titles = {
      'about': 'About Us',
      'logistics': 'Skygate Logistics',
      'travel': 'Skygate Travel',
      'contact': 'Contact Us'
    };
    return titles[path] || path.charAt(0).toUpperCase() + path.slice(1);
  };

  const getPageIcon = (path) => {
    const icons = {
      'about': (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      ),
      'logistics': (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
        </svg>
      ),
      'travel': (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
        </svg>
      ),
      'contact': (
        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
        </svg>
      )
    };
    return icons[path] || (
      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
      </svg>
    );
  };

  if (pathSegments.length === 0) {
    return null; // Don't show breadcrumbs on homepage
  }

  return (
    <nav className={`bg-white/80 backdrop-blur-sm border-b border-gray-200/50 ${className}`} aria-label="Breadcrumb">
      <div className="container-premium">
        <div className="flex items-center space-x-2 py-4">
          {/* Home */}
          <Link 
            to="/" 
            className="flex items-center text-sm text-gray-500 hover:text-primary-600 transition-colors duration-200"
          >
            <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
            </svg>
            Home
          </Link>

          {/* Separator */}
          <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>

          {/* Current Page */}
          <div className="flex items-center text-sm text-gray-900 font-medium">
            {getPageIcon(pathSegments[0])}
            <span className="ml-1">{getPageTitle(pathSegments[0])}</span>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Breadcrumbs;
